mapper.select:
select id,dwmc,dz,fr,cjsj,gxsj,jgdm,lxr,sjhm,gszz,bzj,sjfa,zbwj,is_delete,hd_id
from t_js_bm bm left join t_js_hd hd on( bm.hd_id = hd.id )

    bm.is_delete == 0
     and id = #{map.id} 
     and dwmc = #{map.dwmc} 
     and dz = #{map.dz} 
     and fr = #{map.fr} 
     and jgdm = #{map.jgdm} 
     and lxr = #{map.lxr} 
     and sjhm = #{map.sjhm} 
     and gszz = #{map.gszz} 
     and bzj = #{map.bzj} 
     and sjfa = #{map.sjfa} 
     and zbwj = #{map.zbwj} 
     and hd_id = #{map.hdId} 



        order by ${map.sort} ${map.order}


        order by id asc
