mapper.select:
select bm.id,dwmc,dz,fr,bm.cjsj,bm.gxsj,bm.gszzmc,jgdm,lxr,sjhm,gszz,bzj,sjfa,zbwj,bm.is_delete,hd_id,hd.hdmc as hdmc,shzt,hdlx,hdzt,user_id,yh.yhm as yhm
from t_js_bm bm left join t_js_hd hd on( bm.hd_id = hd.id ) left join t_yh yh on (yh.id=bm.user_id)

    bm.is_delete = 0
     and user_id = #{map.userId} 
     and id = #{map.id} 
     and gszzmc = #{map.gszzmc} 
     and hd_id = #{map.hdId} 
     and bm.dwmc like concat('%',trim(#{map.dwmc}),'%') 
     and dz = #{map.dz} 
     and fr = #{map.fr} 
     and jgdm = #{map.jgdm} 
     and lxr = #{map.lxr} 
     and sjhm = #{map.sjhm} 
     and gszz = #{map.gszz} 
     and bzj = #{map.bzj} 
     and sjfa = #{map.sjfa} 
     and zbwj = #{map.zbwj} 
     and shzt = #{map.shzt} 
     and hd_id = #{map.hdId} 
     and hd.hdmc = #{map.hdmc} 
     and hd.hdzt = #{map.hdzt} 
     and hd.hdlx = #{map.hdlx} 



        order by ${map.sort} ${map.order}


        order by id desc
