mapper.select:
select rc.id,rc.cjsj,rc.gxsj,jjsj,jjje,rc.bm_id,cg_id,xq_id,ppmc,cjf,rc.is_delete
from t_js_cj_rc rc
    left join t_js_xq xq on( xq.id = rc.xq_id )
    left join t_js_cg cg on( cg.id = rc.cg_id )

    rc.is_delete = 0
     and id = #{map.id} 
     and jjsj = #{map.jjsj} 
     and jjje = #{map.jjje} 
     and rc.bm_id = #{map.bmId} 
     and cg_id = #{map.cgId} 
     and xq_id = #{map.xqId} 
     and ppmc = #{map.ppmc} 
     and cjf = #{map.cjf} 



        order by ${map.sort} ${map.order}


        order by id desc

select rc.id,rc.cjsj,rc.gxsj,jjsj,jjje,rc.bm_id,cg_id,xq_id,ppmc,cjf,rc.is_delete,hd.hdkssj as hdkssj,hd.hdjssj as hdjssj
from t_js_cj_rc rc
left join t_js_xq xq on( xq.id = rc.xq_id ) left join t_js_hd hd on( hd.id = xq.jshd_id)
left join t_js_cg cg on( cg.id = rc.cg_id ) left join t_js_hd hd on( hd.id = cg.jshd_id)

    rc.is_delete = 0
     and rc.id = #{map.id} 
     and rc.jjsj = #{map.jjsj} 
     and rc.jjje = #{map.jjje} 
     and rc.bm_id = #{map.bmId} 
     and rc.cg_id = #{map.cgId} 
     and rc.xq_id = #{map.xqId} 
     and rc.ppmc = #{map.ppmc} 
     and rc.cjf = #{map.cjf} 
     and hd.id = #{map.jshdId} 



        order by ${map.sort} ${map.order}


        order by id desc

select rc.id,rc.cjsj,rc.gxsj,rc.jjsj,rc.jjje,rc.bm_id,rc.cg_id,rc.xq_id,rc.ppmc,rc.is_delete,yh.yhm as yhm,bm.dwmc as cjf,hd.hdkssj as hdkssj,hd.hdjssj as hdjssj
from t_js_cj_rc rc
left join t_js_bm bm on( bm.id = rc.bm_id ) left join t_yh yh on( yh.id = bm.user_id ) left join t_js_xq xq on( xq.id = rc.xq_id ) left join t_js_hd hd on( hd.id = xq.jshd_id)
left join t_js_bm bm on( bm.id = rc.bm_id ) left join t_yh yh on( yh.id = bm.user_id ) left join t_js_cg cg on( cg.id = rc.cg_id ) left join t_js_hd hd on( hd.id = cg.jshd_id)

    rc.is_delete = 0
     and rc.id = #{map.id} 
     and rc.jjsj = #{map.jjsj} 
     and rc.jjje = #{map.jjje} 
     and rc.bm_id = #{map.bmId} 
     and rc.cg_id = #{map.cgId} 
     and rc.xq_id = #{map.xqId} 
     and rc.ppmc = #{map.ppmc} 
     and rc.cjf = #{map.cjf} 
     and hd.id = #{map.jshdId} 



        order by ${map.sort} ${map.order}


        order by id desc
