mapper.select:
		select hd.id,hd.hdmc,hd.hdlx,hd.hdjj,hd.hdgz,hd.hdkssj,hd.hdjssj,hd.hdzt,hd.cjsj,hd.zxdh,hd.hdfzr,hd.hdnr,hd.hdfbzt,hd.bmrs,hd.hdfs,hd.hdtp,hd.hddqsj,hd.is_bm
		from t_js_hd hd

			hd.is_delete = 0

				and hd.id in (
				SELECT	bm.hd_id
				FROM
				t_js_bm bm
				LEFT JOIN
				t_js_hd hd
				ON (bm.hd_id = hd.id)
				where bm.user_id = #{map.userId}
				)

		  		   and hd.id = #{map.id} 
			       and hd.hdlx = #{map.hdlx} 
		  		   and hd.hdmc = #{map.name} 
			       and hd.hdtp = #{map.hdtp} 
		  		   and hd.hdjj = #{map.hdjj} 
		  		   and hd.hdgz = #{map.hdgz} 
		  		   and hd.hdkssj = #{map.hdkssj} 
		  		   and hd.hdjssj = #{map.hdjssj} 
		  		   and hd.hdzt = #{map.hdzt} 
					 and hd.hdfbzt = #{map.hdfbzt} 
		  		   and hd.cjsj = #{map.cjsj} 
			       and hd.hdfs = #{map.hdfs} 
			       and hd.is_bm = #{map.isBm} 



                order by ${map.sort} ${map.order}


                order by id desc

select hd.id,hd.hdmc,hd.hdlx,hd.hdjj,hd.hdgz,hd.hdkssj,hd.hdjssj,hd.hdzt,hd.cjsj,hd.zxdh,hd.hdfzr,hd.hdnr,hd.hdfbzt,hd.hdfs,hd.hdtp,hd.hddqsj,count(DISTINCT bm.id) as bmrs,hd.is_bm
from t_js_hd hd LEFT JOIN t_js_bm bm on (hd.id = bm.hd_id)

	hd.is_delete = 0
	and bm.user_id= #{map.userId}
	 and hd.hdlx = #{map.hdlx} 
	 and hd.hdmc = #{map.name} 
	 and hd.hdtp = #{map.hdtp} 
	 and hd.hdjj = #{map.hdjj} 
	 and hd.hdgz = #{map.hdgz} 
	 and hd.hdkssj = #{map.hdkssj} 
	 and hd.hdjssj = #{map.hdjssj} 
	 and hd.hdzt = #{map.hdzt} 
	 and hd.cjsj = #{map.cjsj} 
	 and hd.hdfs = #{map.hdfs} 
	 and hd.is_bm = #{map.isBm} 
	 and hd.hdfbzt = #{map.hdfbzt} 

GROUP BY hd.id


		order by ${map.sort} ${map.order}


		order by hd.id desc

SELECT	hd.id,hd.hdmc,hd.hdlx,hd.hdjj,hd.hdgz,hd.hdkssj,hd.hdjssj,hd.hdzt,hd.cjsj,hd.zxdh,hd.hdfzr,hd.hdnr,hd.hdfbzt,hd.bmrs,hd.hdfs,hd.hdtp,hd.hddqsj,hd.is_bm
FROM
t_js_hd hd
LEFT JOIN (
SELECT
*
FROM
t_js_bm bm
GROUP BY
bm.hd_id,bm.id
) bms ON (bms.hd_id = hd.id)

	hd.is_delete = 0
	 and bms.user_id = #{map.userId} 
	 and hd.id = #{map.id} 
	 and hd.hdtp = #{map.hdtp} 
	 and hd.hdmc = #{map.name} 
	 and hd.hdlx = #{map.hdlx} 
	 and hd.hdjj = #{map.hdjj} 
	 and hd.hdgz = #{map.hdgz} 
	 and hd.hdkssj = #{map.hdkssj} 
	 and hd.hdjssj = #{map.hdjssj} 
	 and hd.hdzt = #{map.hdzt} 
	 and hd.hdfs = #{map.hdfs} 
	 and hd.cjsj = #{map.cjsj} 
	 and hd.is_bm = #{map.isBm} 
	 and hd.hdfbzt = #{map.hdfbzt} 



		order by ${map.sort} ${map.order}


		order by id desc
