mapper.select:
select
`id`,`xm`,`sfzh`,`xb`,`dp`,`xl`,`byyx`,`sszy`,`dz`,`fwly`,`zynlrd`,`zyzsbh`,`zsffsj`,`pxxs`,`pxxf`,`nlzp`,`fwnr`,`fwal`,`cgcx`,`nlpj`,`grtp`,`sp`
from t_js_jj

    and id = #{map.id}
    and xm = #{map.xm}
    and sfzh = #{map.sfzh}
    and xb = #{map.xb}
    and dp = #{map.dp}
    and xl = #{map.xl}
    and byyx = #{map.byyx}
    and sszy = #{map.sszy}
    and dz = #{map.dz}
    and fwly = #{map.fwly}
    and zynlrd = #{map.zynlrd}
    and zyzsbh = #{map.zyzsbh}
    and zsffsj = #{map.zsffsj}
    and pxxs = #{map.pxxs}
    and pxxf = #{map.pxxf}
    and nlzp = #{map.nlzp}
    and fwnr = #{map.fwnr}
    and fwal = #{map.fwal}
    and cgcx = #{map.cgcx}
    and nlpj = #{map.nlpj}
    and grtp = #{map.grtp}
    and sp = #{map.sp}



        order by ${map.sort} ${map.order}


        order by id desc
