mapper.select:
select kz.id,kz.cjsj,kz.gxsj,lcsj,kz.hdzt,dqzgjg,fdjg,jjjgzt,lcdj,cg_id,xq_id,kz.dj,kz.bm_id
from t_js_lc_kz kz
    left join t_js_xq xq on( xq.id = kz.xq_id )
    left join t_js_cg cg on( cg.id = kz.cg_id )

    kz.is_delete = 0
     and id = #{map.id} 
     and cjsj = #{map.cjsj} 
     and gxsj = #{map.gxsj} 

     and lcsj = #{map.lcsj} 
     and dj = #{map.dj} 
     and hdzt = #{map.hdzt} 
     and xq_id = #{map.xqId} 
     and cg_id = #{map.cgId} 
     and lcdj = #{map.lcdj} 
     and jjjgzt = #{map.jjjgzt} 
     and fdjg = #{map.fdjg} 
     and dqzgjg = #{map.dqzgjg} 



        order by ${map.sort} ${map.order}


        order by id desc
