mapper.update:
UPDATE t_js_sh sh left join t_js_cg cg on cg.id = sh.cg_id left join t_js_xq xq on xq.id = sh.xq_id
cg.is_delete = 1, xq.is_delete = 1, sh.is_delete = 1
Where xq_id = #{xqId} and sh.xq_id = #{xqId} cg_id = #{cgId} and sh.cg_id = #{cgId}

mapper.select:
		select `id`,`lx`,`cgxq_id`,`fbshzt`,`hdsqzt`,`fbwtgsm`,`hdsqwtgsm`,`cjsj`,`gxsj`,`release_assistance_status`,`assistance_status`,`assistance_remark` from t_js_sh

		  		   and id = #{map.id} 
		  		   and lx = #{map.lx} 
		  		   and cgxq_id = #{map.cgxqId} 
		  		   and fbshzt = #{map.fbshzt} 
		  		   and hdsqzt = #{map.hdsqzt} 
		  		   and fbwtgsm = #{map.fbwtgsm} 
		  		   and hdsqwtgsm = #{map.hdsqwtgsm} 
		  		   and cjsj = #{map.cjsj} 
		  		   and gxsj = #{map.gxsj} 



                order by ${map.sort} ${map.order}


                order by id desc
