mapper.select:
select hd.id as id,cjsj,gxsj,hdmc,hdjj,hdtp,hdtpmc,bmjzsj,hdzt,bmkssj,hdjssj,hdkssj,hdfbzt,hdnr,lyqy,czqy,zplb,zxdh,hdgz,hdlbt,hdxq,fwbnr
from t_ly_hd hd


    hd.is_delete = 0
     and hd.hdmc = #{map.hdmc} 
     and hd.hdtp = #{map.hdtp} 
     and hd.hdtpmc = #{map.hdtpmc} 
     and hd.hdzt = #{map.hdzt} 
     and hd.hdnr = #{map.hdnr} 
     and hd.bmjzsj = #{map.bmjzsj} 
     and hd.hdfbzt = #{map.hdfbzt} 
     and hd.zxdh = #{map.zxdh} 
     and hd.hdgz = #{map.hdgz} 
     and hd.hdlbt = #{map.hdlbt} 
     and hd.hdxq = #{map.hdxq} 



        order by ${map.sort} ${map.order}


        order by id desc

select hd.id as id,cjsj,gxsj,hdmc,hdjj,hdtp,hdtpmc,bmjzsj,hdzt,bmkssj,hdjssj,hdkssj,hdfbzt,hdnr,lyqy,czqy,zplb,zxdh,hdgz,hdlbt,hdxq,fwbnr
from t_ly_hd hd


    hd.is_delete = 0
     and hd.hdmc = #{map.hdmc} 
     and hd.hdtp = #{map.hdtp} 
     and hd.hdtpmc = #{map.hdtpmc} 
     and hd.hdzt = #{map.hdzt} 
     and hd.hdnr = #{map.hdnr} 
     and hd.bmjzsj = #{map.bmjzsj} 
     and hd.hdfbzt = #{map.hdfbzt} 



        order by ${map.sort} ${map.order}


