mapper.select:
select
ly.id as id,
ly.cjsj,
ly.gxsj,
ly.lyxq,
ly.qymc,
ly.lytp,
ly.lytpmc,
ly.hd_id,
ly.lybt,
ly.lyjj,
ly.zbdz,
ly.qylx,
ly.lxfs,
ly.fbzt,
ly.lysp,
ly.lyspmc,
ly.lykssj,
ly.lydz,
hd.hdmc
from t_ly_ly ly left join t_ly_hd hd on (hd.id = ly.hd_id)


    ly.is_delete = 0
     and ly.lyxq = #{map.lyxq} 
     and ly.qymc = #{map.qymc} 
     and ly.lxfs = #{map.lxfs} 
     and ly.fbzt = #{map.fbzt} 
     and ly.qylx = #{map.qylx} 
     and ly.lytp = #{map.lytp} 
     and ly.lytpmc = #{map.lytpmc} 
     and ly.hd_id = #{map.hdId} 
     and ly.lybt like concat('%',trim(#{map.lybt}),'%') 
     and ly.lyjj = #{map.lyjj} 
     and ly.zbdz = #{map.zbdz} 



        order by ${map.sort} ${map.order}


        order by id desc

