mapper.select:
select ry.id as id,ry.zj_id,ry.gxsj,ry.cjsj,ry.hd_id,ry.zxkssj,ry.zxjssj,ry.zxlx,hd.hdmc as hdmc,zj.tx as tx,zj.xm as xm,zj.dh as dh,zj.dz as dz
from t_ly_ry ry left join t_ly_hd hd on (hd.id = ry.hd_id) left join t_zj zj on (zj.id = ry.zj_id)


    ry.is_delete = 0
     and ry.zj_id = #{map.zjId} 
     and ry.hd_id = #{map.hdId} 
     and ry.zxkssj = #{map.zxkssj} 
     and ry.zxkssj = #{map.zxkssj} 



        order by ${map.sort} ${map.order}


        order by id desc

select ry.id as id,ry.zj_id,ry.gxsj,ry.cjsj,ry.hd_id,ry.zxkssj,ry.zxjssj,ry.zxlx,hd.hdmc as hdmc,zj.tx as tx,zj.xm as xm,zj.dh as dh,zj.dz as dz
from t_ly_ry ry left join t_ly_hd hd on (hd.id = ry.hd_id) left join t_zj zj on (zj.id = ry.zj_id)


    ry.is_delete = 0
     and ry.zj_id = #{map.zjId} 
     and ry.hd_id = #{map.hdId} 
     and ry.zxkssj = #{map.zxkssj} 
     and ry.zxkssj = #{map.zxkssj} 



        order by ${map.sort} ${map.order}


        order by id desc
