mapper.select:
select zp.id as id, zp.zpmc, zp.cjsj, zp.gxsj, zp.zpjj, zp.zplb, zp.zplx, hd.hdmc, sjzd.zdmc as lymc, zp.dwmc, zp.hzjg, zp.lxfs, zp.hzjg, zp.ly_id, zp.lb_id, zp.hd_id, zp.crf, zp.xygl, zp.xyglmc, zp.fbshzt, zp.lxr, zp.zzjgdm, zp.fr, zp.zj, zp.dzyx, zp.dz, zp.yxq, zp.gjc, zp.gjz, zp.fbshbz, zp.hzfs, zp.yxhzdw, zp.yxhzzj, zp.zptp, zp.zptpmc, zp.sjhm, zp.zpsp, zp.zpspmc, zp.yxqkssj, zp.yxqjssj, zp.zpxq, zp.jszb, zp.remarks, zp.cgwcsj from t_ly_zp zp left join t_sjzd sjzd on (sjzd.id = zp.ly_id) left join t_ly_zw zw on (zw.id = zp.zw_id) left join t_ly_hd hd on (hd.id = zp.hd_id)
zp.is_delete = 0 and (zp.fbshzt = 3 or zp.fbshzt = 5) and zp.zpjj = #{map.zpjj} and zp.ly_id = #{map.lyId} and zp.lb_id = #{map.lbId} and zp.zplb = #{map.zplb} and zp.zpmc like concat('%',trim(#{map.zpmc}),'%') and zp.zplx = #{map.zplx} and zp.fbshzt = #{map.fbshzt} and hd.id = #{map.hdId}
order by ${map.sort} ${map.order}
order by id desc

select zp.id as id, zp.zpmc, zp.cjsj, zp.gxsj, zp.zpjj, zp.zplb, zp.zplx, hd.hdmc, sjzd.zdmc as lymc, zp.dwmc, zp.hzjg, zp.lxfs, zp.hzjg, zp.ly_id, zp.lb_id, zp.hd_id, zp.crf, zp.xygl, zp.xyglmc, zp.fbshzt, zp.lxr, zp.zzjgdm, zp.fr, zp.zj, zp.dzyx, zp.dz, zp.yxq, zp.gjc, zp.gjz, zp.fbshbz, zp.hzfs, zp.yxhzdw, zp.yxhzzj, zp.zptp, zp.zptpmc, zp.sjhm, zp.zpsp, zp.zpspmc, zp.yxqkssj, zp.yxqjssj, zp.zpxq, zp.jszb, zp.remarks, zp.cgwcsj from t_ly_zp zp left join t_sjzd sjzd on (sjzd.id = zp.ly_id) left join t_ly_zw zw on (zw.id = zp.zw_id) left join t_ly_hd hd on (hd.id = zp.hd_id)
zp.is_delete = 0 and (zp.fbshzt = 3 or zp.fbshzt = 5) and zp.zpjj = #{map.zpjj} and zp.ly_id = #{map.lyId} and zp.lb_id = #{map.lbId} and zp.zplb = #{map.zplb} and zp.zpmc like concat('%',trim(#{map.zpmc}),'%') and zp.zplx = #{map.zplx} and zp.fbshzt = #{map.fbshzt} and hd.id = #{map.hdId}
order by ${map.sort} ${map.order}
order by id desc
