select zw.id as id,zw.cjsj,zw.gxsj,zw.zwjj,zw.hd_id,hd.hdmc,zw.zwmc,zw.zwtp,zw.zwtpmc,zw.sjzd_id,zd.zdmc as zdmc
from t_ly_zw zw left join t_ly_hd hd on (hd.id = zw.hd_id) left join t_sjzd zd on (zd.id = zw.sjzd_id)


    zw.is_delete = 0
     and zw.zwjj = #{map.zwjj} 
     and hd.hdmc = #{map.hdmc} 
     and zw.zwmc like concat('%',trim(#{map.zwmc}),'%') 
     and zw.hd_id = #{map.hdId} 



        order by ${map.sort} ${map.order}


        order by id desc
