mapper.select:
select count(zp.id) as zpCount, dqzw.id, dqzw.cjsj, dqzw.gxsj, dqzw.zwjj, hd.id AS hd_id, hd.hdmc, dqzw.zwmc, dqzw.zwtp, dqzw.zwtpmc, dqzw.sjzd_id, dqzd.zdmc AS zdmc FROM t_ly_zp zp LEFT JOIN ( SELECT zw.id AS id, zw.cjsj, zw.gxsj, zw.zwjj, hd.id AS hd_id, hd.hdmc, zw.zwmc, zw.zwtp, zw.zwtpmc, zw.sjzd_id, zd.zdmc AS zdmc FROM t_ly_zw zw LEFT JOIN t_zw_hz zwhz ON (zwhz.zw_id = zw.id) LEFT JOIN t_ly_hz hz ON (zwhz.hz_id = hz.id) LEFT JOIN t_ly_hd hd ON (hz.hd_id = hd.id) LEFT JOIN t_sjzd zd ON (zd.id = zw.sjzd_id)
zw.is_delete = 0 and zw.zwjj = #{map.zwjj} and hd.hdmc = #{map.hdmc} and zw.zwmc like concat('%',trim(#{map.zwmc}),'%') and hd.id = #{map.hdId}
) dqzw on(dqzw.id = zp.zw_id) left join t_ly_hd hd on (hd.id = zp.hd_id) LEFT JOIN t_sjzd dqzd ON (dqzd.id = dqzw.sjzd_id)
dqzw.id is not null and zp.is_delete = 0 and zw.zwjj = #{map.zwjj} and hd.hdmc = #{map.hdmc} and zw.zwmc like concat('%',trim(#{map.zwmc}),'%') and hd.id = #{map.hdId}
GROUP BY dqzw.id
order by ${map.sort} ${map.order}
order by id desc

select zw.id as id, zw.cjsj, zw.gxsj, zw.zwjj, hd.id as hd_id, hd.hdmc, zw.zwmc, zw.zwtp, zw.zwtpmc, zw.sjzd_id, zd.zdmc as zdmc from t_ly_zw zw left join t_zw_hz zwhz on (zwhz.zw_id = zw.id) left join t_ly_hz hz on (zwhz.hz_id = hz.id) left join t_ly_hd hd on (hz.hd_id = hd.id) left join t_sjzd zd on (zd.id = zw.sjzd_id) left join t_ly_zp zp on (zp.zw_id = zw.id and zp.hd_id = hd.id)
zw.is_delete = 0 and zw.zwjj = #{map.zwjj} and hd.hdmc = #{map.hdmc} and zw.zwmc like concat('%',trim(#{map.zwmc}),'%') and hd.id = #{map.hdId}
order by ${map.sort} ${map.order}
order by id desc

