zw.is_delete = 0 and hd.fjjg_id = #{map.fjjgId} and zw.zwjj = #{map.zwjj} and hd.hdmc = #{map.hdmc} and zw.zwmc like concat('%',trim(#{map.zwmc}),'%') and zw.hd_id = #{map.hdId}
