		  		   and lxrdh = #{map.lxrdh} 
		  		   and zjh = #{map.zjh} 
		  		   and dzyx = #{map.dzyx} 
		  		   and qyzclx = #{map.qyzclx} 
		  		   and qyzz = #{map.qyzz} 
		  		   and zjgm = #{map.zjgm} 
		  		   and sfqy = #{map.sfqy} 
		  		   and gjxmsl = #{map.gjxmsl} 
		  		   and cjxmsl = #{map.cjxmsl} 
		  		   and sjxmsl = #{map.sjxmsl} 
		  		   and xmzje = #{map.xmzje} 
		  		   and cpjj = #{map.cpjj} 
		  		   and gjjsjj = #{map.gjjsjj} 



                order by ${map.sort} ${map.order}


