mapper.select:
		select `id`,`lx`,`cgxq_id`,`fbshzt`,`hdsqzt`,`fbwtgsm`,`hdsqwtgsm`,`cjsj`,`gxsj`,`releaseAssistance_status`,`assistance_status`,`assistance_remark` from t_js_sh

		  		   and id = #{map.id} 
		  		   and lx = #{map.lx} 
		  		   and cgxq_id = #{map.cgxqId} 
		  		   and fbshzt = #{map.fbshzt} 
		  		   and hdsqzt = #{map.hdsqzt} 
		  		   and fbwtgsm = #{map.fbwtgsm} 
		  		   and hdsqwtgsm = #{map.hdsqwtgsm} 
		  		   and cjsj = #{map.cjsj} 
		  		   and gxsj = #{map.gxsj} 



                order by ${map.sort} ${map.order}


                order by id desc
