mapper.select:
select
`id`,`xm`,`sfz`,`xb`,`sr`,`mz`,`dp`,`xl`,`zyjszw`,`dw`,`dz`,`dh`,`zjh`,`sshy`,`csxk`,`zcfx`,`yjcg`,`dzyj`,`yjly`,`zscq`,`zjxm`,`lw`,`zlh`,`bz`
from t_zj

    and id = #{map.id}
    and xm = #{map.xm}
    and sfz = #{map.sfz}
    and xb = #{map.xb}
    and sr = #{map.sr}
    and mz = #{map.mz}
    and dp = #{map.dp}
    and xl = #{map.xl}
    and zyjszw = #{map.zyjszw}
    and dw = #{map.dw}
    and dz = #{map.dz}
    and dh = #{map.dh}
    and zjh = #{map.zjh}
    and sshy = #{map.sshy}
    and csxk = #{map.csxk}
    and zcfx = #{map.zcfx}
    and yjcg = #{map.yjcg}
    and dzyj = #{map.dzyj}
    and yjly = #{map.yjly}
    and zscq = #{map.zscq}
    and zjxm = #{map.zjxm}
    and lw = #{map.lw}
    and zlh = #{map.zlh}
    and bz = #{map.bz}



        order by ${map.sort} ${map.order}


        order by id desc
